-- Niveau
mapObject0 = require("maps/panel0")
map0 = mapObject0.layers[1].data
mapObject1 = require("maps/panel1")
map1 = mapObject1.layers[1].data
twoDimMap = {}
tileTypes = {}

TILE_HEIGHT = 32
TILE_WIDTH = 32

tileSheet = nil
tileTextures = {}

function initMap()
  
  map = mergePanels(map0, map1)
  
  -- Turn the 1D map generated by Tiled to a 2D map
  twoDimMap = turnOneDimTableToTwoDimTable(map, 23*2, 40)
  for i=1,10 do
    tileTypes[i] = "sol"
  end
  
  -- Load textures from tilesheet
  print("game : initiating textures loading")
  tileSheet = love.graphics.newImage("images/tileSheet1.png")
  local nbColumns = tileSheet:getWidth()/TILE_WIDTH
  local nbRows = tileSheet:getHeight()/TILE_HEIGHT
  
  tileTextures[0] = nil
  
  local r, c
  local id = 1
  for r=1,nbRows do
    for c=1,nbColumns do
      tileTextures[id] = love.graphics.newQuad(
        (c-1)*TILE_WIDTH,
        (r-1)*TILE_HEIGHT,
        TILE_WIDTH,
        TILE_HEIGHT,
        tileSheet:getWidth(),
        tileSheet:getHeight()
        )
      id = id + 1
    end
  end
  print("game : textures successfully loaded")
end

function drawMap()
  -- Draw map
  local row, col
  local x, y
  
  x = 0
  y = 0
  for row = 1, 23*2 do
    for col = 1, 40 do
      -- Draw tile
      local tile = twoDimMap[row][col]
      local texQuad = tileTextures[tile]
      if tile > 0 then
        love.graphics.draw(tileSheet, texQuad, x, y, 0, 1, 1)
      end
      x = x + TILE_WIDTH
    end
    x = 0
    y = y + TILE_HEIGHT
  end
end

function mergePanels(panel0, panel1)
  local mergedMap
  mergedMap = panel0
  for _,v in ipairs(panel1) do 
    table.insert(mergedMap, v)
  end
  
  --for key, value in ipairs(mergedMap) do
  --      print('\t', key, value)
  --end
  
  return mergedMap
end
