-- Niveau
map0 = require("maps/panel1")
map = map0.layers[1].data
twoDimMap = {}
tileTypes = {}

TILE_HEIGHT = 32
TILE_WIDTH = 32

tileSheet = nil
tileTextures = {}

function initMap()
  -- Turn the 1D map generated by Tiled to a 2D map
  twoDimMap = turnOneDimTableToTwoDimTable(map, 23, 40)
  tileTypes[0] = "air"
  tileTypes[2] = "sol"
  
  -- Load textures from tilesheet
  print("game : initiating textures loading")
  tileSheet = love.graphics.newImage("images/tileSheet1.png")
  local nbColumns = tileSheet:getWidth()/TILE_WIDTH
  local nbRows = tileSheet:getHeight()/TILE_HEIGHT
  
  tileTextures[0] = nil
  
  local r, c
  local id = 1
  for r=1,nbRows do
    for c=1,nbColumns do
      tileTextures[id] = love.graphics.newQuad(
        (c-1)*TILE_WIDTH,
        (r-1)*TILE_HEIGHT,
        TILE_WIDTH,
        TILE_HEIGHT,
        tileSheet:getWidth(),
        tileSheet:getHeight()
        )
      id = id + 1
    end
  end
  print("game : textures successfully loaded")
end

function drawMap()
  -- Draw map
  local row, col
  local x, y
  
  x = 0
  y = 0
  for row = 1, 23 do
    for col = 1, 40 do
      -- Draw tile
      local tile = twoDimMap[row][col]
      local texQuad = tileTextures[tile]
      if tile > 0 then
        love.graphics.draw(tileSheet, texQuad, x, y, 0, 1, 1)
      end
      x = x + TILE_WIDTH
    end
    x = 0
    y = y + TILE_HEIGHT
  end
end

